<?php
if ( !defined( 'ABSPATH' ) ) {
    exit; // Exit if accessed directly
}

$imageTrue = 'data:image/png;base64,';

$imageFalse = 'data:image/png;base64,';

if ( apply_filters( 'wordpresscrm_view_images_boolean', false ) ) {
    if ( $value ) {
        ?><img
        src="<?php echo apply_filters( 'wordpresscrm_view_images_boolean_true', $imageTrue ); ?>"><?php
    } else {
        ?><img
        src="<?php echo apply_filters( 'wordpresscrm_view_images_boolean_false', $imageFalse ); ?>"><?php
    }
} else {
    echo $formatted_value;
}
